property.value:
local _bpm = 120;
local _bpmStartTime = getMillis();

local _snapshotButtonID = nil;
local _groupComponentID = nil;

local _morphingDurationRadioButtonIndex = nil;  -- index of the radio button for the snapshot duration load
local _morphingDelayRadioButtonIndex = nil;     -- index of the radio button to start load snapshot 

local _progressBarWidth = nil;                  -- max width of the progress bar
local _progressBarComponent = nil;              -- reference to the progress bar box


local _delayTimerEnabled = false;               -- delay to wait when start load snapshot enabled (true = a snapshot button was touched) 
local _delayTimerStartTime = 0;                 -- time in ms when the snapshot button is pressed 
local _delayTimerDuration = 0;                  -- start load snapshot at _delayTimerStartTime + _delayTimerDuration

local _morphingTimerEnabled = false;            -- load (morphing) snapshot in progress
local _morphingTimerStartTime = 0;              -- load (morphing) snapshot start time (ms)
local _morphingTimerDuration = 0;               -- load (morphing) duration

local _morphingStartValues = {};                -- morphing start values of all components (radials)
local _morphingTargetValues = {};               -- morphing target values of all components (radials)
local _components = {};                         -- copy of all components (radials) to manage during morphing snapshot



function onReceiveNotify(action, value)
  if(action == 'setBpm') then
    setBpm(value); -- {bpm, bpmStartMs}    
  elseif (action == 'saveSnapshot') then
    saveSnapshot(value); -- {snapshotButtonID, groupComponentID}
  elseif(action == 'resetSnapshot') then
    resetSnapshot(value); -- {snapshotButtonID}   
  elseif(action == 'playSnapshot') then
    playSnapshot(value); -- {snapshotButtonID, groupComponentID, durationIndex, startIndex, progressBarID, progressBarWidth}  
  end
end


function setBpm(value) 
  _bpm = value[1];
  _bpmStartTime = value[2];
end


-- save all components value in the snapshot button tag as value1|value2|...|valueX|
function saveSnapshot(value)
  local snapshotButtonID = value[1];
  local groupComponentID = value[2];
  local snapshotButton = root:findByID(snapshotButtonID, true);
  local components = root:findByID(groupComponentID, true):findAllByType(ControlType.RADIAL);
  local nbComponents = #components;
  local separator = '|';

  local saveMessage = '';

  for i=1, nbComponents do
    saveMessage = saveMessage .. components[i].values.x .. separator;
  end

  snapshotButton.tag = saveMessage;
  snapshotButton.outlineStyle = OutlineStyle.FULL;
end


-- reset snapshot button tag
function resetSnapshot(value) 
  local snapshotButtonID = value[1];  
  local snapshotButton = root:findByID(snapshotButtonID, true);
  snapshotButton.tag = '';  
  snapshotButton.outlineStyle = OutlineStyle.CORNERS;
end


-- save all parameters before start the delay timer
function playSnapshot(value) 
  _snapshotButtonID = value[1];
  _groupComponentID = value[2];
  _morphingDurationRadioButtonIndex = value[3];
  _morphingDelayRadioButtonIndex = value[4];
  local progressBarID = value[5];
  _progressBarWidth = value[6];

  -- set progress bar to 0
  _progressBarComponent = root:findByID(progressBarID, true);
  _progressBarComponent.frame.w = 0;

  -- start delay timer
  _delayTimerEnabled = true;
  _delayTimerStartTime = getMillis();

  -- compute delay duration
  _delayTimerDuration = 0;
  if(_morphingDelayRadioButtonIndex == 1) then _delayTimerDuration = 1; end
  if(_morphingDelayRadioButtonIndex == 2) then _delayTimerDuration = 2; end
  if(_morphingDelayRadioButtonIndex == 3) then _delayTimerDuration = 4; end
  if(_morphingDelayRadioButtonIndex == 4) then _delayTimerDuration = 8; end
  local durationMultiplier = 240000 / _bpm;  
  _delayTimerDuration = _delayTimerDuration * durationMultiplier; -- ms  

  local beat_ms = 60000 / _bpm;
  local phase = (getMillis() - _bpmStartTime) % beat_ms;

  _delayTimerDuration = _delayTimerDuration - phase;
end



function update()
  doStartDelay();
  doMorphing();
end


-- watch if the delay timer is in progress
-- change the progress bar width during delay time
-- when the delay timer if finish start the morphing
function doStartDelay()
  if not _delayTimerEnabled then return end

  local now = getMillis();
  local elapsed = now - _delayTimerStartTime;

  if(_delayTimerDuration <= 0) then
    _progressBarComponent.frame.w = _progressBarWidth;
  else
    _progressBarComponent.frame.w = (elapsed / _delayTimerDuration) * _progressBarWidth;
  end

  if(elapsed > _delayTimerDuration) then
    _delayTimerEnabled = false;
    startMorphing();
  end
end


-- save all current components value and target value
-- start the morphing timer
function startMorphing()
  local snapshotButton = root:findByID(_snapshotButtonID, true);
  local components = root:findByID(_groupComponentID, true):findAllByType(ControlType.RADIAL);
  local separator = '|';

  local savedMessage = snapshotButton.tag;

  _morphingTimerEnabled = true;
  _morphingTimerStartTime = getMillis();
  _components = components;

  -- compute morphing duration
  _morphingTimerDuration = 0;
  if(_morphingDurationRadioButtonIndex == 1) then _morphingTimerDuration = 1; end
  if(_morphingDurationRadioButtonIndex == 2) then _morphingTimerDuration = 2; end
  if(_morphingDurationRadioButtonIndex == 3) then _morphingTimerDuration = 4; end
  if(_morphingDurationRadioButtonIndex == 4) then _morphingTimerDuration = 8; end  
  local durationMultiplier = 240000 / _bpm;

  _morphingTimerDuration = _morphingTimerDuration * durationMultiplier; -- ms  

  -- save all current components value
  -- comoute and save all components target value
  local componentIndex = 1;
  for value in string.gmatch(savedMessage, '([^' .. separator .. ']+)') do
    _morphingStartValues[componentIndex] = components[componentIndex].values.x;
    _morphingTargetValues[componentIndex] = tonumber(value) or components[componentIndex].values.x;
    componentIndex = componentIndex + 1;
  end 
end


-- watch if morphing is in progress
-- modify each components value
function doMorphing() 
  if not _morphingTimerEnabled then return end

  local now = getMillis();
  local elapsed = now - _morphingTimerStartTime;
  local t = elapsed / _morphingTimerDuration;

  if t >= 1 then
    t = 1;
    _morphingTimerEnabled = false;
  end

  -- linear interpolation
  local nbComponents = #_components;
  for i=1, nbComponents do
    local component = _components[i];
    local v0 = _morphingStartValues[i] or component.values.x;
    local v1 = _morphingTargetValues[i] or v0;
    local v = v0 + (v1 - v0) * t;
    component.values.x = v;
  end  
end

local resetSnapshotButton = self.parent.children.buttonResetSnapshot;

function onValueChanged(key)
  if((key == 'x') and (self.values.x == 1)) then  
    resetSnapshotButton.values.x = 0;
  end
end

local saveSnapshotButton = self.parent.children.buttonSaveSnapshot;

function onValueChanged(key)
  if((key == 'x') and (self.values.x == 1)) then    
    saveSnapshotButton.values.x = 0;
  end
end

local _saveSnapshotButton = self.parent.parent.children.buttonSaveSnapshot;
local _resetSnapshotButton = self.parent.parent.children.buttonResetSnapshot;
local _durationSnapshotRadio = self.parent.parent.children.radioSnapshotDuration;
local _delaySnapshotRadio = self.parent.parent.children.radioSnapshotDelay;
local _progressBarWidthBox = self.parent.parent.children.boxProgressBarWidth;
local _progressBarBox = self.parent.parent.children.boxProgressBar;

function onValueChanged(key)
  if((key ~= 'x') or (self.values.x ~= 0)) then return end

  if(_resetSnapshotButton.values.x == 1) then
    root:notify('resetSnapshot', {self.ID});
    return;
  end

  if(_saveSnapshotButton.values.x == 1) then
    root:notify('saveSnapshot', {self.ID, self.parent.parent.parent.children.groupRadial.ID});
  else  
    root:notify('playSnapshot', {
      self.ID, 
      self.parent.parent.parent.children.groupRadial.ID, 
      _durationSnapshotRadio.values.x,
      _delaySnapshotRadio.values.x,
      _progressBarBox.ID,
      _progressBarWidthBox.frame.w
    });
  end

  -- change colors  
  local initialColor = Color(1,0,0);
  local buttons = self.parent:findAllByType(ControlType.BUTTON);
  for i=1, #buttons do
    buttons[i].color = initialColor;
  end

  self.color = Color(0,1,0);    

end

