local _buttons = self.parent.children.groupSetMacro:findAllByType(ControlType.BUTTON);
local _components = self.parent.parent.children.groupRadial:findAllByType(ControlType.RADIAL);

local oldValue = -1000;

function onValueChanged(key)
  if(key == 'x') then
    if(oldValue == -1000) then 
      oldValue = self.values.x;       
    else
      local offset = self.values.x - oldValue;

      for i=1, #_components do
        local multiplier = tonumber(_buttons[i].tag or "0") or 0;
        _components[i].values.x = _components[i].values.x + offset * multiplier;
      end

      oldValue = self.values.x;
    end
  end
end

local _buttons = self.parent.children.groupSetMacro:findAllByType(ControlType.BUTTON);
local _components = self.parent.parent.children.groupRadial:findAllByType(ControlType.RADIAL);

