local _times = {};
local _maxTimes = 6;
local _delayToReset = 1000;
local _bpm = tonumber(self.parent.children.textBpmValue.values.text);
local _bpmStartMs = getMillis();


local _flash_ms = 120;        -- flash duration (ms)
local _baseColor = Color(self.color);
local _flashColor = Color(
    math.min(_baseColor.r * 1.8, 1.0),
    math.min(_baseColor.g * 1.8, 1.0),
    math.min(_baseColor.b * 1.8, 1.0),
    _baseColor.a
  );

function init()
  root:notify('setBpm', {_bpm, _bpmStartMs});
end

function onValueChanged(key)
  if((key ~= 'x') or (self.values.x ~= 0)) then return end

  local now = getMillis();

  -- Reset if too long
  if #_times > 0 then
    local delta = now - _times[#_times];

    if delta > _delayToReset then
      _times = {};
      _bpm = nil;
      _bpmStartMs = nil;
      self.parent.children.textBpmValue.values.text = '---';
    end
  end

  _bpmStartMs = now;

  table.insert(_times, now);

  -- keep only _maxTimes elements      
  if #_times > _maxTimes then
    table.remove(_times, 1);
  end

  -- compute BPM
  if #_times >= 2 then
    local sum = 0;
    for i = 2, #_times do
      sum = sum + (_times[i] - _times[i-1]);
    end

    local avg = sum / (#_times - 1);
    _bpm = math.floor(60000 / avg + 0.5);
    root:notify('setBpm', {_bpm, _bpmStartMs});

    self.parent.children.textBpmValue.values.text = tostring(_bpm);
  end
end

function update()  
  if _bpm and _bpm > 0 and _bpmStartMs then
    local beat_ms = 60000 / _bpm;
    local phase = (getMillis() - _bpmStartMs) % beat_ms;

    if phase < _flash_ms then
      self.color = _flashColor;
    else
      self.color = _baseColor;
    end
  else    
    self.color = _baseColor;
  end
end

--local endl = '\r\n';  -- for debug set the end of line readable
local endl = '##';
local separator = '^^';

function onReceiveNotify(action, value)
  if(action == 'load') then
    load(value);      
  elseif(action =='save') then
    save();
  end
end

-- SAVE FUNCTION
function save(key)
  local message = endl .. savePages();

  print(message);  
end


function savePages()
  local message = '';

  local pages = root:findByName('pager1');
  for i=1, #pages.children do  
    message = message .. savePage(pages.children[i].ID);
    message = message .. endl;
    message = message .. endl;
  end

  return message;
end



function savePage(pageID)
  local message = '';

  local page = root:findByID(pageID, true);
  --logProperties(page);
  message = message .. 'PAGE: ' .. separator .. page.ID .. separator .. page.tabLabel .. separator .. saveColor(page.tabColorOff) .. endl;

  local groupSnapshotButton = page:findByName('groupSnapshotButton', true);
  message = message .. saveSnapshotButtons(groupSnapshotButton.ID);

  local groupMacro1 = page:findByName('groupMacro1', true);
  local groupSetMacro1 = groupMacro1:findByName('groupSetMacro', true);
  message = message .. saveMacro(groupSetMacro1.ID);

  local groupMacro1 = page:findByName('groupMacro2', true);
  local groupSetMacro1 = groupMacro1:findByName('groupSetMacro', true);
  message = message .. saveMacro(groupSetMacro1.ID);

  local groupRadial = page:findByName('groupRadial', true);
  message = message .. saveRadials(groupRadial.ID);

  return message;
end

function saveSnapshotButtons(groupSnapshotButtonID)
  local message = '';

  local groupSnapshotButton = root:findByID(groupSnapshotButtonID, true);
  message = message .. 'SNAPSHOT BUTTON: ' .. groupSnapshotButton.ID .. endl;

  local buttons = groupSnapshotButton:findAllByType(ControlType.BUTTON);
  for i=1, #buttons do
    message = message .. saveSnapshotButton(buttons[i].ID);
  end

  return message;
end



function saveMacro(groupSetMacroID)
  local message = '';

  local groupSetMacro = root:findByID(groupSetMacroID, true);
  message = message .. 'GROUP SET MACRO: ' .. groupSetMacro.ID .. endl;

  local buttons = groupSetMacro:findAllByType(ControlType.BUTTON);
  for i=1, #buttons do
    message = message .. saveSetMacroButton(buttons[i].ID);
  end

  return message;
end


function saveRadials(groupRadial)
  local message = '';

  local groupRadials = root:findByID(groupRadial, true);
  message = message .. 'GROUP RADIAL: ' .. groupRadials.ID .. endl;

  local texts = groupRadials:findAllByType(ControlType.TEXT);
  for i=1, #texts do
    message = message .. saveRadialText(texts[i].ID);
  end

  local labels = groupRadials:findAllByType(ControlType.LABEL);
  for i=1, #labels do
    message = message .. saveRadialText(labels[i].ID);
  end

  local radials = groupRadials:findAllByType(ControlType.RADIAL);
  for i=1, #radials do
    message = message .. saveRadial(radials[i].ID);
  end

  return message;
end

