-- LOAD FUNCTION
function load(savedMessage)
  for line in string.gmatch(savedMessage, '(.-)' .. endl) do
    if string.find(line, '^PAGE') then
      loadPage(line);
    end

    if string.find(line, '^ButtonSnapshot') then
      loadSnapshotButton(line);
    end

    if string.find(line, '^ButtonSetMacro') then
      loadSetMacroButton(line);
    end

    if string.find(line, '^RadialText') then
      loadRadialText(line);
    end    

    if string.find(line, '^RadialController') then
      loadRadial(line);
    end      
  end  
end


function loadPage(savedMessage)
  --print(savedMessage);

  local values = {}
  for v in string.gmatch(savedMessage .. separator, '(.-)' .. separator) do 
    table.insert(values, v)
  end

  local id = values[2];
  local label = values[3];
  local color = values[4];


  local page = root:findByID(id, true);
  if(page ~= nil) then
    page.tabLabel = label;
    page.tabColorOff = loadColor(color);
  else
    print('Page not found: ', savedMessage);
  end  
end


function loadSnapshotButton(savedMessage)
  --print(savedMessage);

  local values = {}
  for v in string.gmatch(savedMessage .. separator, '(.-)' .. separator) do 
    table.insert(values, v)
  end

  local id = values[2];
  local tag = values[3];


  local button = root:findByID(id, true);
  if(button ~= nil) then
    button.tag = tag;
    if(tag ~= '') then
      button.outlineStyle = OutlineStyle.FULL;
    else
      button.outlineStyle = OutlineStyle.CORNERS;
    end
  else
    print('SnapshotButton not found: ', savedMessage);
  end  
end


function loadSetMacroButton(savedMessage)
  --print(savedMessage);

  local values = {}
  for v in string.gmatch(savedMessage .. separator, '(.-)' .. separator) do 
    table.insert(values, v)
  end

  local id = values[2];
  local tag = values[3];


  local button = root:findByID(id, true);
  if(button ~= nil) then
    button.tag = tag;

    local v = tonumber(tag or '0') or 0
    if v == 0 then
      button.values.x = 0;
      button.outlineStyle = OutlineStyle.FULL;
    else
      button.values.x = 1
      if(v == -1) then
        button.outlineStyle = OutlineStyle.CORNERS;
      else
        button.outlineStyle = OutlineStyle.FULL;
      end
    end
  else
    print('SetMacroButton not found: ', savedMessage);
  end  
end


