-- HELPER functions
function saveColor(color)
  return '(' .. color.r .. ',' .. color.g .. ','.. color.b .. ','.. color.a .. ')';
end


function loadColor(color)
  local r, g, b, a = color:match("%(([%d%.]+),([%d%.]+),([%d%.]+),([%d%.]+)%)");
  return Color(tonumber(r), tonumber(g), tonumber(b), tonumber(a));
end


function logProperties(component)
  local keys = component.properties.keys;
  for i = 1, #keys do
    local name = keys[i];
    local value = component.properties[name];
    print(name, value);
  end
end

function logMessages(component)
  local midiMessages = component.messages.MIDI;
  for i = 1, #midiMessages do
    local message = midiMessages[i];
    local enabled = message.enabled;
    local receive = message.receive;
    local channel = message.channel;
    local controller = message.controller;
    local value = message.value;
    print('midi message: ', enabled, receive, channel, controller, value);
  end
end

function onValueChanged(key)
  if((key ~= 'x') or (self.values.x ~= 0)) then return end

  self.parent:notify('save');  
end
